mapper.select:
SELECT o.open_id AS orderOpenId, o.sesame_shop_open_id AS shopOpenId, o.sesame_shop_name AS shopName, oc.sesame_commodity_open_id AS commodityOpenId, oc.sesame_commodity_name AS commodityName, oc.sesame_specifications_open_id AS specificationsOpenId, oc.sesame_specifications_name AS specificationsName, oc.sesame_specifications_thumbnail AS specificationsThumbnail, oc.num, oc.pay_price AS spePayPrice, oc.`status`, o.pay_price, oc.`id` AS orderCommodityId FROM sesame_order o LEFT JOIN sesame_order_commodity oc ON o.`id` = oc.sesame_order_id WHERE o.created_id = #{userId} AND o.del_flag = 1 AND o.sesame_shop_id IS NOT NULL
AND o.`status` = #{status}
ORDER BY o.created_time DESC, o.sesame_shop_name, oc.`sort`
